(lambda table: print(f"Mean: {(mean := int(sum(table) / len(table)))}\nAway from Mean: {(away := [int(abs(mean - item)) for item in table])}\nVariance: {(variance := sum([item ** 2 for item in away]) / len(table)):.2f}\nStandard Deviation: {__import__('math').sqrt(variance):.2f}"))([int(item) for item in open(__import__("os").path.join(__import__("os").path.dirname(__import__("os").path.abspath(__file__)), "data.txt")).read().split(", ")])